const aiResponse = $input.first().json.output;

// Detectar si hay un JSON dentro de un bloque de código markdown
const jsonBlockMatch = aiResponse.match(/```json\s*([\s\S]*?)\s*```/);
const hasJson = jsonBlockMatch !== null;

// Extraer el JSON si existe
let orderData = null;
let isPedidoConfirmado = false;

if (hasJson) {
    try {
        // Extraer solo el contenido JSON del bloque
        const jsonString = jsonBlockMatch[1].trim();
        orderData = JSON.parse(jsonString);

        // Validar que el JSON tiene los campos requeridos para un pedido confirmado
        if (orderData && 
            orderData.pedido_id && 
            orderData.ventana_id && 
            orderData.lista_compras && 
            orderData.direccion && 
            orderData.zona_domicilio &&
            orderData.valor_oferta &&
            orderData.nombre_completo &&
            orderData.ciudad) {
            isPedidoConfirmado = true;
        }
    } catch (error) {
        console.log('Error parsing JSON:', error);
        orderData = null;
    }
}

// Limpiar la respuesta para el cliente (quitar todo el bloque JSON)
let cleanResponse = aiResponse;
if (hasJson) {
    cleanResponse = aiResponse.replace(jsonBlockMatch[0], '').trim();
    // Quitar líneas vacías extra que puedan quedar
    cleanResponse = cleanResponse.replace(/\n\s*\n+/g, '\n\n').trim();
}

// Estructurar la salida con los nuevos campos
return [{
    output: cleanResponse,
    is_pedido_confirmado: isPedidoConfirmado,
    order_data: orderData
}];

