// Procesar Ofertas Ventana - OPTIMIZADO
const ventanaData = $('Start').first().json;
const ofertasRecibidas = $('Buscar Ofertas Recibidas').all(); // Ofertas de Supabase

// Solo retornar los datos esenciales para el algoritmo
return {
  valor_oferta: ventanaData.valor_oferta,
  ofertas_recibidas: ofertasRecibidas,
  pedido_id: $('Start').first().json.pedido_id,
  ventana_id: $('Start').first().json.ventana_id,
  numero_completo: ventanaData.numero_completo
};

// Algoritmo de selecci√≥n
const datos = $input.first().json;
const ofertasRecibidas = datos.ofertas_recibidas || [];
const ofertaOriginal = datos.valor_oferta;

function seleccionarMejorOferta(ofertas, ofertaOriginal) {
  if (!ofertas || ofertas.length === 0) {
    return {
      tiene_ofertas: false,
      tipo_seleccion: 'sin_ofertas'
    };
  }

  // Extraer los datos de la propiedad 'json' de cada oferta
  const ofertasNormalizadas = ofertas.map(ofertaWrapper => {
    const oferta = ofertaWrapper.json || ofertaWrapper;
    return {
      ...oferta,
      valor_oferta: parseFloat(oferta.valor_oferta) || 0,
      fecha_oferta: new Date(oferta.fecha_oferta)
    };
  });

  // Separar aceptaciones y contraofertas v√°lidas
  const aceptaciones = ofertasNormalizadas.filter(o => 
    o.valor_oferta <= ofertaOriginal && o.tipo_oferta === 'aceptacion'
  );

  const contraofertas = ofertasNormalizadas.filter(o => 
    o.valor_oferta > ofertaOriginal && o.tipo_oferta === 'contraoferta'
  );

  // Prioridad 1: Aceptaciones del precio original
  if (aceptaciones.length > 0) {
    // Ordenar por fecha de oferta (m√°s antigua primero - primera oferta gana)
    const mejorAceptacion = aceptaciones.sort((a, b) => 
      a.fecha_oferta - b.fecha_oferta
    )[0];

    return {
      tiene_ofertas: true,
      mejor_oferta: mejorAceptacion,
      tipo_seleccion: 'aceptacion',
      valor_final: ofertaOriginal,
      acepta_automatico: true
    };
  }

  // Prioridad 2: Mejor contraoferta (precio m√°s bajo, y en caso de empate, primera oferta)
  if (contraofertas.length > 0) {
    const mejorContraoferta = contraofertas.sort((a, b) => {
      // Primer criterio: menor precio
      if (a.valor_oferta !== b.valor_oferta) {
        return a.valor_oferta - b.valor_oferta;
      }
      // Segundo criterio (desempate): fecha m√°s antigua (primera oferta gana)
      return a.fecha_oferta - b.fecha_oferta;
    })[0];

    return {
      tiene_ofertas: true,
      mejor_oferta: mejorContraoferta,
      tipo_seleccion: 'contraoferta',
      valor_final: mejorContraoferta.valor_oferta,
      acepta_automatico: false
    };
  }

  return {
    tiene_ofertas: false,
    tipo_seleccion: 'sin_ofertas'
  };
}

// Ejecutar selecci√≥n
const resultado = seleccionarMejorOferta(ofertasRecibidas, ofertaOriginal);

// Estructurar la salida seg√∫n el formato requerido
if (resultado.tiene_ofertas && resultado.mejor_oferta) {
  return {
    pedido_id: datos.pedido_id,
    ventana_id: datos.ventana_id,
    oferta_id: resultado.mejor_oferta.oferta_id,
    tipo_seleccion: resultado.tipo_seleccion,
    valor_final: resultado.valor_final,
    nombre_domiciliario: resultado.mejor_oferta.nombre_domiciliario,
    numero_domiciliario: resultado.mejor_oferta.numero_domiciliario,
    calificacion_domiciliario: resultado.mejor_oferta.calificacion_domiciliario
  };
} else {
  // Cuando no hay ofertas v√°lidas
  return {
    pedido_id: datos.pedido_id,
    ventana_id: datos.ventana_id,
    oferta_id: null,
    tipo_seleccion: resultado.tipo_seleccion,
    valor_final: null,
    nombre_domiciliario: null,
    numero_domiciliario: null,
    calificacion_domiciliario: null
  };
}

// Clasificar domiciliarios - VERSI√ìN OPTIMIZADA
try {
  console.log('=== INICIANDO CLASIFICACI√ìN DOMICILIARIOS ===');

  // 1. Obtener resultado del algoritmo de selecci√≥n
  const resultadoVentana = $('Algoritmo Selecci√≥n').first().json;

  // 2. Obtener ofertas recibidas del nodo anterior
  const ofertasRecibidas = $('Buscar Ofertas Recibidas').all();

  // 3. Obtener domiciliarios notificados del Start (viene como string, convertir a array)
  const startData = $('Start').first().json;
  const domiciliariosNotificadosStr = startData.domiciliarios_notificados || '';
  let domiciliariosNotificados = [];

  try {
    domiciliariosNotificados = domiciliariosNotificadosStr.split(',').map(num => num.trim()).filter(num => num !== '');
  } catch (parseError) {
    console.log('Error parseando domiciliarios_notificados:', parseError.message);
    domiciliariosNotificados = [];
  }

  if (domiciliariosNotificados.length === 0) {
    return {
      clasificacion: { seleccionado: [], rechazados: [], no_participaron: [] },
      tiene_ofertas: false,
      stats: { total_domiciliarios: 0, participaron: 0, no_participaron: 0 },
      error: 'No hay domiciliarios notificados'
    };
  }

  // Extraer datos del resultado
  const tieneOfertas = resultadoVentana?.tipo_seleccion !== 'sin_ofertas';
  const domiciliarioSeleccionado = resultadoVentana?.numero_domiciliario || null;
  const pedidoId = resultadoVentana?.pedido_id || 'SIN_ID';
  const ventanaId = resultadoVentana?.ventana_id || 'SIN_VENTANA';

  // Crear set de domiciliarios que ofertan
  const domiciliariosQueOfertan = new Set();

  // Procesar ofertas recibidas para identificar qui√©nes participaron
  ofertasRecibidas.forEach((ofertaWrapper, index) => {
    try {
      const oferta = ofertaWrapper?.json;
      if (oferta && oferta.numero_domiciliario) {
        domiciliariosQueOfertan.add(oferta.numero_domiciliario);
      }
    } catch (err) {
      console.log(`Error procesando oferta ${index}:`, err.message);
    }
  });

  // Crear mapas para encontrar ofertas por domiciliario
  const ofertasPorDomiciliario = new Map();

  // Mapear ofertas por n√∫mero de domiciliario
  ofertasRecibidas.forEach((ofertaWrapper) => {
    try {
      const oferta = ofertaWrapper?.json;
      if (oferta && oferta.numero_domiciliario && oferta.oferta_id) {
        ofertasPorDomiciliario.set(oferta.numero_domiciliario, oferta.oferta_id);
      }
    } catch (err) {
      console.log(`Error mapeando oferta:`, err.message);
    }
  });

  // Arrays para clasificaci√≥n simplificada
  const rechazados = [];
  const noParticiparon = [];

  // Procesar todos los domiciliarios notificados
  domiciliariosNotificados.forEach((numeroDomiciliario) => {
    try {
      if (!numeroDomiciliario) {
        return;
      }

      // Saltar el domiciliario seleccionado
      if (numeroDomiciliario === domiciliarioSeleccionado) {
        return;
      }

      if (domiciliariosQueOfertan.has(numeroDomiciliario)) {
        // Particip√≥ pero no fue seleccionado - incluir n√∫mero y oferta_id
        const ofertaId = ofertasPorDomiciliario.get(numeroDomiciliario);
        rechazados.push({
          numero_domiciliario: numeroDomiciliario,
          oferta_id: ofertaId || null
        });
      } else {
        // No particip√≥ - solo el n√∫mero
        noParticiparon.push(numeroDomiciliario);
      }
    } catch (err) {
      console.log(`Error procesando domiciliario:`, err.message);
    }
  });

  console.log('=== CLASIFICACI√ìN COMPLETADA ===');
  console.log('Rechazados:', rechazados.length);
  console.log('No participaron:', noParticiparon.length);

  // Retornar resultado simplificado
  return {
    rechazados,
    no_participaron: noParticiparon
  };

} catch (error) {
  console.error('ERROR EN CLASIFICAR DOMICILIARIOS:', error.message);

  return {
    rechazados: [],
    no_participaron: [],
    error: error.message
  };
}

// Script para procesar domiciliarios que no participaron
try {
  console.log('=== PROCESANDO NO PARTICIPARON ===');

  // Obtener datos del nodo de clasificaci√≥n anterior
  const clasificacionData = $('Clasificar Domiciliarios').first().json;
  const noParticiparon = clasificacionData.no_participaron || [];

  // Convertir cada n√∫mero en un item separado
  const items = noParticiparon.map((numeroDomiciliario) => {
    console.log('Procesando no particip√≥:', numeroDomiciliario);

    return {
      numero_domiciliario: numeroDomiciliario
    };
  });

  console.log(`Total de items creados: ${items.length}`);
  return items;

} catch (error) {
  console.error('ERROR PROCESANDO NO PARTICIPARON:', error.message);
  return [{
    error: error.message,
  }];
}

// Procesar Domiciliarios Rechazados
try {
  console.log('=== PROCESANDO DOMICILIARIOS RECHAZADOS ===');

  // Obtener datos del nodo anterior (Clasificar Domiciliarios)
  const clasificacionData = $('Clasificar Domiciliarios').first().json;
  const rechazados = clasificacionData?.rechazados || [];

  // Crear un item separado para cada domiciliario rechazado
  const itemsRechazados = rechazados.map((rechazado) => {
    return {
      numero_domiciliario: rechazado.numero_domiciliario,
      oferta_id: rechazado.oferta_id
    };
  });

  console.log(`Procesados ${itemsRechazados.length} domiciliarios rechazados`);

  return itemsRechazados;

} catch (error) {
  console.error('ERROR PROCESANDO RECHAZADOS:', error.message);
  return [{
    error: error.message
  }];
}

