parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "oneOf": [
    {
      "title": "TOOL_CALL",
      "description": "El agente ha identificado una acción que requiere ejecutar una tool",
      "type": "object",
      "properties": {
        "type": { 
          "const": "TOOL_CALL",
          "description": "Tipo de respuesta: llamada a herramienta"
        },
        "accion": {
          "type": "string",
          "enum": [
            "aceptar_pedido",
            "contraofertar_pedido",
            "validar_entrega",
            "cambiar_estado",
            "obtener_estado"
          ],
          "description": "Acción identificada del usuario"
        },
        "tool": {
          "type": "string",
          "enum": [
            "aceptar_pedido",
            "contraofertar_pedido",
            "validar_entrega_pedido",
            "cambiar_estado_domiciliario",
            "obtener_estado_domiciliario"
          ],
          "description": "Nombre exacto de la tool a ejecutar"
        },
        "args": {
          "type": "object",
          "properties": {
            "numero_domiciliario": { 
              "type": "string",
              "pattern": "^57[0-9]{10}$",
              "description": "Número de celular colombiano con prefijo 57"
            },
            "pedido_id": { 
              "type": ["integer", "null"],
              "minimum": 1,
              "description": "ID del pedido (null si no aplica)"
            },
            "valor_contraoferta": { 
              "type": ["integer", "null"],
              "minimum": 1000,
              "description": "Valor de contraoferta en pesos colombianos (null si no aplica)"
            },
            "codigo_verificacion": {
              "oneOf": [
                { 
                  "type": "string", 
                  "pattern": "^[0-9]{4}$",
                  "description": "Código de 4 dígitos numéricos"
                },
                { "type": "null" }
              ]
            },
            "estado_nuevo": {
              "oneOf": [
                { 
                  "type": "string", 
                  "enum": ["Activo", "Inactivo"],
                  "description": "Nuevo estado del domiciliario"
                },
                { "type": "null" }
              ]
            }
          },
          "required": ["numero_domiciliario", "pedido_id", "valor_contraoferta", "codigo_verificacion", "estado_nuevo"],
          "additionalProperties": false
        },
        "missing_fields": { 
          "type": "array", 
          "items": { 
            "type": "string",
            "enum": ["pedido_id", "valor_contraoferta", "codigo_verificacion", "estado_nuevo"]
          },
          "description": "Lista de campos que faltan para ejecutar la tool (vacío si están todos)"
        },
        "confidence": { 
          "type": ["number", "null"],
          "minimum": 0,
          "maximum": 1,
          "description": "Nivel de confianza en la interpretación (0-1)"
        },
        "trace_id": { 
          "type": "string",
          "description": "ID de trazabilidad único para esta operación"
        }
      },
      "required": ["type", "accion", "tool", "args", "missing_fields", "trace_id"],
      "additionalProperties": false
    },
    {
      "title": "ASK_USER",
      "description": "El agente necesita más información del usuario",
      "type": "object",
      "properties": {
        "type": { 
          "const": "ASK_USER",
          "description": "Tipo de respuesta: solicitar datos al usuario"
        },
        "accion": { 
          "type": "string",
          "enum": [
            "aceptar_pedido",
            "contraofertar_pedido",
            "validar_entrega",
            "cambiar_estado",
            "obtener_estado",
            "ayuda",
            "menu",
            "desconocido"
          ],
          "description": "Acción que se está intentando completar"
        },
        "missing_fields": { 
          "type": "array", 
          "items": { 
            "type": "string",
            "enum": ["pedido_id", "valor_contraoferta", "codigo_verificacion", "estado_nuevo", "confirmacion"]
          },
          "minItems": 1,
          "description": "Campos faltantes que se deben solicitar"
        },
        "question": { 
          "type": "string",
          "minLength": 10,
          "maxLength": 500,
          "description": "Pregunta específica para solicitar los datos faltantes (sin emojis, técnica)"
        },
        "context": {
          "type": "object",
          "description": "Contexto parcial recolectado hasta ahora",
          "properties": {
            "pedido_id": { "type": ["integer", "null"] },
            "valor_contraoferta": { "type": ["integer", "null"] },
            "codigo_verificacion": { "type": ["string", "null"] }
          }
        },
        "trace_id": { 
          "type": "string",
          "description": "ID de trazabilidad único"
        }
      },
      "required": ["type", "accion", "missing_fields", "question", "trace_id"],
      "additionalProperties": false
    },
    {
      "title": "RESULT",
      "description": "El agente ha procesado el resultado de una tool y genera respuesta final",
      "type": "object",
      "properties": {
        "type": { 
          "const": "RESULT",
          "description": "Tipo de respuesta: resultado final"
        },
        "accion": {
          "type": "string",
          "enum": [
            "aceptar_pedido",
            "contraofertar_pedido",
            "validar_entrega",
            "cambiar_estado",
            "obtener_estado",
            "ayuda",
            "menu",
            "desconocido"
          ],
          "description": "Acción ejecutada"
        },
        "status": {
          "type": "string",
          "enum": [
            "ok",
            "not_found",
            "window_closed",
            "invalid_code",
            "value_too_low",
            "conflict",
            "already_done",
            "error"
          ],
          "description": "Estado del resultado de la operación"
        },
        "args": {
          "type": "object",
          "properties": {
            "numero_domiciliario": { 
              "type": "string",
              "pattern": "^57[0-9]{10}$"
            },
            "pedido_id": { 
              "type": ["integer", "null"],
              "minimum": 1
            },
            "valor_contraoferta": { 
              "type": ["integer", "null"],
              "minimum": 1000
            },
            "codigo_verificacion": {
              "oneOf": [
                { "type": "string", "pattern": "^[0-9]{4}$" },
                { "type": "null" }
              ]
            },
            "estado_nuevo": {
              "oneOf": [
                { "type": "string", "enum": ["Activo", "Inactivo"] },
                { "type": "null" }
              ]
            }
          },
          "required": ["numero_domiciliario", "pedido_id", "valor_contraoferta", "codigo_verificacion", "estado_nuevo"],
          "additionalProperties": false
        },
        "data": { 
          "type": "object",
          "description": "Datos estructurados retornados por la tool",
          "properties": {
            "pedido_id": { "type": ["integer", "null"] },
            "ventana_id": { "type": ["string", "null"] },
            "valor_cliente": { "type": ["integer", "null"] },
            "valor_contraoferta": { "type": ["integer", "null"] },
            "estado_nuevo": { "type": ["string", "null"] },
            "estado_anterior": { "type": ["string", "null"] },
            "fecha_oferta": { "type": ["string", "null"], "format": "date-time" },
            "fecha_entrega": { "type": ["string", "null"], "format": "date-time" },
            "direccion": { "type": ["string", "null"] },
            "ciudad": { "type": ["string", "null"] },
            "codigo_verificacion": { "type": ["string", "null"] },
            "ventana": {
              "type": ["object", "null"],
              "properties": {
                "inicio": { "type": "string", "format": "date-time" },
                "fin": { "type": "string", "format": "date-time" },
                "estado": { "type": "string", "enum": ["abierta", "cerrada"] }
              }
            }
          }
        },
        "error_code": {
          "type": ["string", "null"],
          "enum": [
            null,
            "WINDOW_CLOSED",
            "VALUE_TOO_LOW",
            "NOT_FOUND",
            "ALREADY_DELIVERED",
            "INVALID_CODE",
            "CONFLICT",
            "DATABASE_ERROR",
            "TIMEOUT"
          ],
          "description": "Código de error técnico si status != ok"
        },
        "request_id": { 
          "type": "string",
          "description": "ID de la request (execution_id de n8n)"
        },
        "trace_id": { 
          "type": "string",
          "description": "ID de trazabilidad único"
        }
      },
      "required": ["type", "accion", "status", "args", "data", "request_id", "trace_id"],
      "additionalProperties": false
    }
  ]
}
